function _track (page, ext?) {
    _doTrack('#TrackFrame', page, ext);
};
function _subTrack (page, ext?) {
    _doTrack('#SubTrackFrame', page, ext);
};
function _doTrack (frame, page?, ext?) {
    var dest = '/track/' + page + '?on_page=' + encodeURIComponent(location.pathname);
    if (ext) {
        dest += ('&' + ext);
    }

    console.log('track: ' + dest);
    $('#Track').find(frame).attr('src', dest);
};